import React, { useState } from "react"
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import { PropaneSharp } from "@mui/icons-material";

export default function BasicTextFields(props) {

    const [name, setName] = useState("");
    const [address, setAddress] = useState("");
    // const [studentInfo, setStudentInfo] = useState({name:"",addres:""});

    function handleNameChange(event){
        setName(event.target.value);
        // var prevAddress = studentInfo.addres;
        // setStudentInfo({
        //     name: name,
        //     addres: prevAddress
        // })
    }    

    function handleAddressChange(event){
        setAddress(event.target.value);
        // var prevName = studentInfo.name;
        // setStudentInfo({
        //     name: prevName,
        //     addres: address
        // })
    }

    function handleClick()
    {
        props.onAdd({
            name : name,
            address : address
        });
    }

    return (
        <Box
        component="form"
        sx={{
            '& > :not(style)': { m: 1 },
        }}
        noValidate
        autoComplete="off"
        >
            <h1 style={{fontFamily:"sans-serif"}} >Add Student</h1>
        <TextField onChange={handleNameChange} value={name} id="filled-basic" label="Name" variant="filled" />
        <TextField onChange={handleAddressChange} value={address} id="filled-basic" label="Address" variant="filled" />
        <Stack spacing={2} direction="row">
            <Button onClick={handleClick} variant="contained">Add</Button>
        </Stack>
        </Box>
    );
}
